import{_ as i,r as b,o as d,c as l,b as a,d as c,w as o,F as v,e as t,f as n,z as r,A as f}from"./index-55fd179e.js";const m="/vue3-QA/assets/founders-0fbff7af.png";const p={},g=e=>(r("data-v-5b9abe29"),e=e(),f(),e),u=t('<section class="mb-5 banner" style="height:380px;" data-v-5b9abe29><div class="container" data-v-5b9abe29><div class="row justify-content-center" data-v-5b9abe29><div class="col-sm-9 col-md-7 col-lg-5" data-v-5b9abe29><h1 class="banner-plan fs-3 fs-lg-2" data-v-5b9abe29>關於我們</h1></div></div></div></section>',1),h={class:"container"},_={"aria-label":"breadcrumb"},x={class:"breadcrumb mb-8 mb-lg-20"},w={class:"breadcrumb-item"},j=g(()=>a("li",{class:"breadcrumb-item active","aria-current":"page"},"關於我們",-1)),y=t('<section data-v-5b9abe29><div class="text-center" data-v-5b9abe29><h2 class="block-title position-relative fs-3 fs-lg-2 fw-lg-bold mb-8 mb-lg-15" data-v-5b9abe29>我們的理念</h2></div><div class="row justify-content-between mb-4 mb-lg-17" data-v-5b9abe29><div class="col-lg-6 mb-4 mb-lg-0" data-v-5b9abe29><img src="https://images.unsplash.com/photo-1498837167922-ddd27525d352?ixlib=rb-4.0.3&amp;ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&amp;auto=format&amp;fit=crop&amp;w=1470&amp;q=80" alt="" class="img-fluid object-fit-cover" width="600" height="400" data-v-5b9abe29></div><div class="col-lg-5" data-v-5b9abe29><div class="d-flex align-items-center h-100" data-v-5b9abe29><p class="fs-6 fs-lg-5 text-justify" data-v-5b9abe29> 好蔬房創立於 2016 年，從創立至今始終堅持有好的品質，才能擁有顧客對好蔬房長期的信任。根據研究指出，為了獲得最佳的營養健康，至少 80% 的飲食應由植物組成，這就是為什麼我們只選擇最好的成分，所有這些成分都是 100% 植物性的。 </p></div></div></div><div class="row flex-lg-row-reverse justify-content-between mb-4 mb-lg-17" data-v-5b9abe29><div class="col-lg-6 mb-4 mb-lg-0" data-v-5b9abe29><div class="text-end" data-v-5b9abe29><img src="https://images.unsplash.com/photo-1569420077790-afb136b3bb8c?ixlib=rb-4.0.3&amp;ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&amp;auto=format&amp;fit=crop&amp;w=1489&amp;q=80" alt="" class="img-fluid object-fit-cover" width="600" height="400" data-v-5b9abe29></div></div><div class="col-lg-5" data-v-5b9abe29><div class="d-flex align-items-center h-100" data-v-5b9abe29><div class="fs-6 fs-lg-5 text-justify" data-v-5b9abe29><p data-v-5b9abe29>我們不使用高溫烹煮食物，而是採用裸食的方式料理食物，讓食物保有原有的必需營養素、高蛋白質、和高膳食纖維，使您一整天都精力充沛，而且不會讓您感到沉重有負擔。</p><p data-v-5b9abe29>我們的食物不添加任何香料、增味劑、色素或防腐劑。您在我們的菜單上不會找到任何您不了解的成分，只會看到完整、乾淨、單純的成分。</p></div></div></div></div><div class="row justify-content-between mb-8 mb-lg-20" data-v-5b9abe29><div class="col-lg-6 mb-4 mb-lg-0" data-v-5b9abe29><img src="https://images.unsplash.com/uploads/1412443967017d95e2723/2919e13b?ixlib=rb-4.0.3&amp;ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&amp;auto=format&amp;fit=crop&amp;w=1470&amp;q=80" alt="" class="img-fluid object-fit-cover" width="600" height="400" data-v-5b9abe29></div><div class="col-lg-5" data-v-5b9abe29><div class="d-flex align-items-center h-100" data-v-5b9abe29><p class="fs-6 fs-lg-5 text-justify" data-v-5b9abe29> 我們只從對環境和社會負責的供應商採購材料。可持續發展是我們堅守的核心精神，這意味著我們會持續不斷的推動永續餐桌的理念，並且會不斷探索我們可以改進的地方和實踐對環境友善的作法，讓我們在品嚐美味、健康食物的同時，也能為我們的地球盡一份環保的心力。 </p></div></div></div><div class="text-center" data-v-5b9abe29><h2 class="block-title position-relative fs-3 fs-lg-2 fw-lg-bold mb-8 mb-lg-15" data-v-5b9abe29>我們是誰</h2></div><div class="row flex-lg-row-reverse justify-content-between mb-8 mb-lg-20" data-v-5b9abe29><div class="col-lg-5" data-v-5b9abe29><figure class="figure" data-v-5b9abe29><img src="'+m+'" class="figure-img img-fluid" alt="founders" data-v-5b9abe29><figcaption class="figure-caption text-center" data-v-5b9abe29>我們的創辦人 Lisa、Gordon 和 Brian</figcaption></figure></div><div class="col-lg-6" data-v-5b9abe29><div class="d-flex align-items-center h-100" data-v-5b9abe29><div class="fs-6 fs-lg-5 text-justify" data-v-5b9abe29><p data-v-5b9abe29> 我們是好蔬房的三位創辦人 Lisa、Gordon 和 Brian， 本身都是純素愛好者，過去身為上班族的我們因為找不到滿意的純素外食，通常都是自己準備便當居多，我們深知要準備純素料理對一般忙碌的上班族而言相當不易，許多人因此放棄了純素飲食，這是相當可惜的。 </p><p data-v-5b9abe29> 我們是在一次的純素社群聚會中認識後，三人一拍即合，因此萌生了一起創業的念頭，希望能為忙碌的現代人提供純素、美味又快速的料理，這就是好蔬房創立的開始。我們對於全植物飲食和環保議題充滿熱情，致力於將食物最根本的養分和美味呈現給大家，期待能在好蔬房與大家見面喔！ </p></div></div></div></div></section>',1);function B(e,H){const s=b("RouterLink");return d(),l(v,null,[u,a("div",h,[a("nav",_,[a("ol",x,[a("li",w,[c(s,{to:"/",class:"green"},{default:o(()=>[n("首頁")]),_:1})]),j])]),y])],64)}const V=i(p,[["render",B],["__scopeId","data-v-5b9abe29"]]);export{V as default};
